import React, { Component } from 'react';
import Cat from './Cat';
import Dog from './Dog';
import './styles.css';
import VoteCount from './VoteCount';

var self;

class App extends Component {

	constructor(props) {
		super(props);
		this.state = {
			catPictureURL: [],
			dogPictureURL: [],
			catVotes: 0,
			dogVotes: 0,
		};
	}

	componentDidMount() {
		self = this;
		self.loadNewPictures();
	}

	async loadNewPictures() {
		let catURL, dogURL;

		await fetch('https://aws.random.cat/meow')
			.then(res => res.json())
			.then(json => {
				catURL = json.file;
			});

		var found = false;
		while (!found) {
			await fetch('https://random.dog/woof.json')
				.then(res => res.json())
				.then(json => {
					var imgType = json.url.substring(json.url.length - 4, json.url.length).toLowerCase();
					if (imgType === ".jpg" || imgType === ".png") {
						found = true;
						dogURL = json.url;
					}
				});
		}

		self.setState({
			catPictureURL: catURL,
			dogPictureURL: dogURL,
		});
	}

	async addVote(name) {
		let score;
		let getURL = "http://dreamlo.com/lb/5ec2880d0cf2aa0c28423922/json-get/" + name
		await fetch(getURL)
			.then(res => res.json())
			.then(json => {
				score = Number(json.dreamlo.leaderboard.entry.score);
			});

		score++;

		let setURL = "http://dreamlo.com/lb/rB0P6BG4_0y6ODkGEkwiBQyiBo5yCdykaB3uR9hY74_g/add/" +
			name + "/" + toString(score);
		fetch(setURL)

		return score;
	}

	addVoteCat() {
		self.addVote("cat");

		var { catVotes: newCatvotes } = self.state;
		newCatvotes++;
		self.setState({
			catVotes: newCatvotes,
		});
		self.loadNewPictures();
	}
	addVoteDog() {
		var { dogVotes: newDogvotes } = self.state;
		newDogvotes++;
		self.setState({
			dogVotes: newDogvotes,
		});
		self.loadNewPictures();
	}

	render() {
		var { catPictureURL, dogPictureURL, catVotes, dogVotes } = this.state;

		return (
			<div className={"background"}>
				<div className={"pictures"}>
					<Cat picture={catPictureURL} />
					<Dog picture={dogPictureURL} />
				</div>
				<div className={"voteBtns"}>
					<button className={"voteBtn"} onClick={this.addVoteCat}>cat</button>
					<button className={"voteBtn"} onClick={this.addVoteDog}>dog</button>
				</div>
				<div className={"voteCounts"}>
					<VoteCount votes={catVotes} />
					<VoteCount votes={dogVotes} />
				</div>
			</div>
		);
	}

}

export default App;
