import React, { Component } from 'react';
import Cat from './Cat';
import Dog from './Dog';

class App extends Component {

	constructor(props) {
		super(props);
		this.state = {
			catPicture: [],
			dogPicture: [],
		};
	}

	componentDidMount() {
		this.loadPictures();
	}

	loadPictures() {
		var catPictureURL;
		var dogPicture;

		fetch('https://aws.random.cat/meow')
			.then(res => res.json())
			.then(json => {
				catPicture = json.file;
			});

		// for (let i = 0; i < 10; i++) {
		// 	console.log(i);
		// }
		// while (!dogPictureJson) {
		// 	fetch('https://random.dog/woof.json')
		// 		.then(res => res.json())
		// 		.then(json => {
		// 			var imgType = json.url.substring(json.url.length - 4, json.url.length).toLowerCase();
		// 			if (imgType === ".jpg" || imgType === ".png") {
		// 				dogPictureJson = json;
		// 			}
		// 		});
		// }
		fetch('https://random.dog/woof.json')
			.then(res => res.json())
			.then(json => {
				var imgType = json.url.substring(json.url.length - 4, json.url.length).toLowerCase();
				if (imgType === ".jpg" || imgType === ".png") {
					dogPicture = json.url;
				}
			});
		this.setState({
			catPicture: catPicture,
			dogPicture: dogPicture
		});
	}

	render() {
		var { catPicture, dogPicture } = this.state;

		return (
			<>
				<div>
					{catPicture}
				</div>
				<Cat catPictureUrl={catPicture} />
				<Dog dogPictureUrl={dogPicture} />
			</>
		);
	}

}

export default App;
