import React, { Component } from 'react';
import Cat from './Cat';
import Dog from './Dog';

class App extends Component {

	constructor(props) {
		super(props);
		this.state = {
			catPictureURL: [],
			dogPictureURL: [],
		};
	}

	componentDidMount() {
		this.loadPictures();
	}

	async loadPictures() {

		fetch('https://aws.random.cat/meow')
			.then(res => res.json())
			.then(json => {
				this.setState({
					catPictureURL: json.file,
				});
			});

		var found = false;
		while (!found) {
			console.log(1);
			await fetch('https://random.dog/woof.json')
				.then(res => res.json())
				.then(json => {
					var imgType = json.url.substring(json.url.length - 4, json.url.length).toLowerCase();
					if (imgType === ".jpg" || imgType === ".png") {
						found = true;
						this.setState({
							dogPictureURL: json.url,
						});
					}
				});
		}
	}

	render() {
		var { catPictureURL, dogPictureURL } = this.state;

		return (
			<>
				<Cat picture={catPictureURL} />
				<Dog picture={dogPictureURL} />
			</>
		);
	}

}

export default App;
