import React, { Component } from 'react';
import Cat from './Cat';
import Dog from './Dog';
import './styles.css';

class App extends Component {

	constructor(props) {
		super(props);
		this.state = {
			catPictureURL: [],
			dogPictureURL: [],
		};
	}

	componentDidMount() {
		this.loadNewPictures();
	}

	async loadNewPictures() {

		await fetch('https://aws.random.cat/meow')
			.then(res => res.json())
			.then(json => {
				this.setState({
					catPictureURL: json.file,
				});
			});

		var found = false;
		while (!found) {
			await fetch('https://random.dog/woof.json')
				.then(res => res.json())
				.then(json => {
					var imgType = json.url.substring(json.url.length - 4, json.url.length).toLowerCase();
					if (imgType === ".jpg" || imgType === ".png") {
						found = true;
						this.setState({
							dogPictureURL: json.url,
						});
					}
				});
		}
	}

	addVoteCat() {
		console.log("cat");
		this.loadNewPictures
	}
	addVoteDog() {
		console.log("dog");
	}

	render() {
		var { catPictureURL, dogPictureURL } = this.state;

		return (
			<div className={"background"}>
				<div className={"pictures"}>
					<Cat picture={catPictureURL} />
					<Dog picture={dogPictureURL} />
				</div>
				<div className={"voteBtns"}>
					<button className={"voteBtn"} onClick={this.addVoteCat}>cat</button>
					<button className={"voteBtn"} onClick={this.addVoteDog}>dog</button>
				</div>
			</div>
		);
	}

}

export default App;
