import React, { Component } from 'react';
import Cat from './Cat';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      catPicture: []
    };
  }

  componentDidMount() {
    fetch('https://aws.random.cat/meow')
      .then(res => res.json())
      .then(json => {
        this.setState({
          catPicture: json,
          isLoaded: true
        })
      })
  }

  render() {
    var { catPicture, isLoaded } = this.state;

    return (
      <>
        <Cat catPictureUrl={catPicture.file} />
      </>
    );
  }

}

export default App;
