import React, { Component } from 'react';
import Cat from './Cat';
import Dog from './Dog';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      catPicture: [],
      dogPicture: [],
    };
  }

  componentDidMount() {
    fetch('https://aws.random.cat/meow')
      .then(res => res.json())
      .then(json => {
        this.setState({
          catPicture: json
        })
      });

    var foundPicture = false
    while (!foundPicture) {
      fetch('https://random.dog/woof.json')
        .then(res => res.json())
        .then(json => {
          this.setState({
            dogPicture: json
          })
        });
    }
  }

  render() {
    var { catPicture, dogPicture } = this.state;

    return (
      <>
        <Cat catPictureUrl={catPicture.file} />
        <Dog dogPictureUrl={dogPicture.url} />
      </>
    );
  }

}

export default App;
