import React, { Component } from 'react';
import Cat from './Cat';
import Dog from './Dog';

class App extends Component {

	constructor(props) {
		super(props);
		this.state = {
			catPictureURL: [],
			dogPictureURL: [],
		};
	}

	componentDidMount() {
		this.loadPictures();
	}

	loadPictures() {
		var catPicture;
		var dogPicture;

		fetch('https://aws.random.cat/meow')
			.then(res => res.json())
			.then(json => {
				this.setState({
					catPictureURL: catPicture,
				});
			});

		// for (let i = 0; i < 10; i++) {
		// 	console.log(i);
		// }
		// while (!dogPictureJson) {
		// 	fetch('https://random.dog/woof.json')
		// 		.then(res => res.json())
		// 		.then(json => {
		// 			var imgType = json.url.substring(json.url.length - 4, json.url.length).toLowerCase();
		// 			if (imgType === ".jpg" || imgType === ".png") {
		// 				dogPictureJson = json;
		// 			}
		// 		});
		// }
		fetch('https://random.dog/woof.json')
			.then(res => res.json())
			.then(json => {
				var imgType = json.url.substring(json.url.length - 4, json.url.length).toLowerCase();
				if (imgType === ".jpg" || imgType === ".png") {
					dogPicture = json.url;
				}
			});
		this.setState({
			catPictureURL: catPicture,
			dogPictureURL: dogPicture
		});
	}

	render() {
		var { catPictureURL, dogPictureURL } = this.state;

		return (
			<>
				<div>
					{catPictureURL}
				</div>
				<Cat catPictureUrl={catPictureURL} />
				<Dog dogPictureUrl={dogPictureURL} />
			</>
		);
	}

}

export default App;
