import React, { Component } from 'react';
import Cat from './Cat';
import Dog from './Dog';

class App extends Component {

	constructor(props) {
		super(props);
		this.state = {
			catPicture: [],
			dogPicture: [],
		};
	}

	componentDidMount() {
		this.loadPictures();
	}

	loadPictures() {
		var catPictureJson;
		var dogPicture;

		fetch('https://aws.random.cat/meow')
			.then(res => res.json())
			.then(json => {
				this.setState({
					catPicture: json
				})
			});

		while (!dogPicture) {
			fetch('https://random.dog/woof.json')
				.then(res => res.json())
				.then(json => {
					var imgType = json.url.substring(json.url.length - 4, json.url.length);
					if (imgType === ".jpg" || imgType === ".png") {
						console.log("reached")
						this.setState({
							dogPicture: json
						});
					}
				});
		}

		this.setState({
			catPicture: catPicture,
			dogPicture: dogPicture
		});
	}

	render() {
		var { catPicture, dogPicture } = this.state;

		return (
			<>
				<Cat catPictureUrl={catPicture.file} />
				<Dog dogPictureUrl={dogPicture.url} />
			</>
		);
	}

}

export default App;
